import React, { useState } from "react";
import { Input } from "src/components/atoms/Input";
import { Button } from "src/components/atoms/buttons/Button";
import { useSelector } from "react-redux";

export const PostForm = ({ update, setModal, isEdit, editedPost }) => {
  const { items } = useSelector((state) => state.posts);

  const [posts, setPosts] = useState(items || []);

  let array = [
    {
      id: 1,
      title: "Купить молоко",
      body: "Потребуется 2 литра молока.",
    },
    {
      id: 2,
      title: "Купить муку",
      body: "Для приготовления яблочного пирога необходима мука (1 пачка).",
    },
    {
      id: 3,
      title: "Купить творог",
      body: "На завтрак.",
    },
    {
      id: 4,
      title: "Купить сметану и сахар",
      body: "Для творога.",
    },
  ];

  let u = items;

  u?.forEach((el) => {
    console.log(el.title === editedPost.id);

    if (el.id === editedPost?.id) {
      el.title = "Ntw";
      el.body = "Newwwwww";
    }
  }),
    console.log(u);

  // console.log(
  //   setPosts(
  //     (posts || [])?.forEach((el) => {
  //       console.log(el.title === editedPost.id);
  //
  //       if (el.id === editedPost?.id) {
  //         el.title = "Ntw";
  //         el.body = "Newwwwww";
  //       }
  //     }),
  //   ),
  // );

  // console.log(setPosts(posts.splice(0, 1)));

  // console.log(
  //   posts.concat(newPost).forEach((el) => {
  //     if (el.id === editedPost?.id) {
  //       el.title = "Ntw";
  //       el.body = "Newwwwww";
  //     }
  //   }),
  // );

  const [post, setPost] = useState({
    title: editedPost?.title || "",
    body: editedPost?.body || "",
  });

  const updatePost = (e) => {
    e.preventDefault();

    if (!isEdit) {
      const newPost = {
        ...post,
        id: Date.now(),
      };

      update(newPost);
    } else {
      const updatingPosts = items.forEach((el) => {
        if (el.id === editedPost?.id) {
          el.title = post?.title || "";
          el.body = post?.body || "";
        }
      });

      console.log(updatingPosts);
      // update(editedPost);

      // console.log(items?.filter((el) => el.id !== editedPost.id));
    }

    setPost({ title: "", body: "" });

    setModal(false);
  };

  return (
    <form>
      <Input
        value={post.title}
        onChange={(e) => setPost({ ...post, title: e.target.value })}
        type="text"
        placeholder="Название заметки"
      />

      <Input
        value={post.body}
        onChange={(e) => setPost({ ...post, body: e.target.value })}
        type="text"
        placeholder="Описание заметки"
      />

      <Button onClick={updatePost}>Сохранить</Button>
    </form>
  );
};
