import React from "react";
import { Button } from "src/components/atoms/buttons/Button";
import { Box } from "src/components/atoms/Box";
import { Input } from "src/components/atoms/Input";

export const PostItem = ({ remove, number, post }) => (
  <div className="post">
    <div style={{ flexDirection: "row" }} className="post__content">
      <p >
        {number || 0}.</p> <p><Input value={post?.title} />
      </p>

      <Input value={post?.body} />
    </div>

    <Box visible={!!remove} className="post__btns">
      <Button onClick={() => remove(post)}>Удалить</Button>
    </Box>
  </div>
);
