import { createSlice } from "@reduxjs/toolkit";

const defaultItems = [
  {
    id: 1,
    title: "Купить молоко",
    body: "Потребуется 2 литра молока.",
  },
  {
    id: 2,
    title: "Купить муку",
    body: "Для приготовления яблочного пирога необходима мука (1 пачка).",
  },
  {
    id: 3,
    title: "Купить творог",
    body: "На завтрак.",
  },
  {
    id: 4,
    title: "Купить сметану и сахар",
    body: "Для творога.",
  },
];

const initialState = {
  totalCount: 4,
  items: defaultItems,
};

const posts = createSlice({
  name: "posts",
  initialState,
  reducers: {
    addItem(state, action) {
      state.totalCount = initialState.totalCount + 1;
      state.items = action.payload;
    },

    updateItem(state, action) {
      // state.items = state.items.forEach((el) => {
      //   if (el.id === action.payload.id) {
      //     el.title = action.payload.title || "";
      //     el.body = action.payload.body || "";
      //   }
      //   console.log("el title:");
      //   console.log(el.title);
      //   console.log("el body:");
      //   console.log(el.body);
      //   console.log("------------------");
      //   console.log("action.payload.title:");
      //   console.log(action.payload.title);
      //   console.log("action.payload.body:");
      //   console.log(action.payload.body);
      // });


      state.items =  state.items.map((el) => {
        if (el.id === action.payload.id) {
          el.title = action.payload.title || "";
          el.body = action.payload.body || "";
        }
      });
    },

    removeItem(state, action) {
      state.totalCount = initialState.totalCount - 1;
      state.items = state.items.filter((obj) => obj.id !== action.payload);
    },

    clearItems(state) {
      state.items = [];
      state.totalCount = 0;
    },
  },
});

export const { addItem, updateItem, removeItem, clearItems } = posts.actions;

export default posts.reducer;
