import React, { useState } from "react";
import { Input } from "src/components/atoms/Input";
import { Button } from "src/components/atoms/buttons/Button";
import { useSelector } from "react-redux";

export const PostForm = ({ update, setModal, isEdit, selectedPost }) => {
  const { items } = useSelector((state) => state.posts);

  // console.log(
  //   setPosts(
  //     (posts || [])?.forEach((el) => {
  //       console.log(el.title === editedPost.id);
  //
  //       if (el.id === editedPost?.id) {
  //         el.title = "Ntw";
  //         el.body = "Newwwwww";
  //       }
  //     }),
  //   ),
  // );

  // console.log(setPosts(posts.splice(0, 1)));

  const [post, setPost] = useState({
    title: selectedPost?.title || "",
    body: selectedPost?.body || "",
  });

  const updatePost = (e) => {
    e.preventDefault();

    if (!isEdit) {
      const newPost = {
        ...post,
        id: Date.now(),
      };

      update(newPost);
    } else {
      // const updatedPosts = items.map((el) => {
      //   if (el.id === editedPost?.id) {
      //     el.title = post?.title || "";
      //     el.body = post?.body || "";
      //   }
      // });

      console.log(editedPost);

      update(editedPost);
    }

    setPost({ title: "", body: "" });

    setModal(false);
  };

  return (
    <form>
      <Input
        value={post.title}
        onChange={(e) => setPost({ ...post, title: e.target.value })}
        type="text"
        placeholder="Название заметки"
      />

      <Input
        value={post.body}
        onChange={(e) => setPost({ ...post, body: e.target.value })}
        type="text"
        placeholder="Описание заметки"
      />

      <Button onClick={updatePost}>Сохранить</Button>
    </form>
  );
};
